document.getElementById('changePassword').onchange = function(e){
        if(this.checked) {
        	document.getElementById('password').removeAttribute('disabled')
        	document.getElementById('passwordAgain').removeAttribute('disabled')
        }else {
        	document.getElementById('password').setAttribute('disabled', '1')
        	document.getElementById('passwordAgain').setAttribute('disabled', '1')
        }
 
};
